import './header.css';
import { ShopifyContext } from '../ShopifyContext';
import React, { useContext, useEffect } from 'react'
import { BrowserRouter as Router, Routes, Route, Link, useParams, useNavigate } from 'react-router-dom'
import Select from 'react-select';
import cart from './crt.png';



const Header = () => {
  const navigator = useNavigate();
  const { fetchCollections, collections , fetchSearchProducts,key,setkey} = useContext(ShopifyContext);
 
 
  useEffect(() => {
    fetchCollections()
    return () => { }
  }, [fetchCollections])

  const handleChange = (e) => {
    navigator(`/catalog/${e.value}`)
  }

  const searchProducts=()=>{
  fetchSearchProducts(key);
  setkey("");
  navigator('/search')
}

const handleSearchStringChange = (e) => {
  navigator('/search')
}



return (

    <div className="header" style={{ backgroundColor: "cornflowerblue" }}>
      <div class="container" style={{ maxWidth: "100%" }} >
        <div class="row">

        </div>
        <div class="row">
          <div class="col-sm-2">
            <Link to="/home" style={{ float: "left", color: "white", marginLeft: "10px", fontSize: "60px", 
            fontFamily: "Brush Script MT, cursive" }}>MStore</Link>
          </div>
          <div class="col-sm-2">
            <Select className="dropdwn" onChange={handleChange} placeholder="Catalog" options={collections}>

            </Select>
          </div>
          <div class="col-sm-2">
          <div className='searchbar' style={{marginLeft: "10px",height:""}}>
          <input type="text" placeholder="Search" value={key} onChange={handleSearchStringChange}/>
          <button type ="submit" onclick={searchProducts}>Search</button>
          </div>
          </div>
          <div class="col-sm-2">
            <Link to="/cart" style={{ float: "right", color: "#FAEBD7", marginRight: "20px" }}>
              <img src={cart} alt="cartimage"/></Link>
          </div>
        </div>

      </div>

    </div>



  )
}

export default Header;
