import React, { useContext, useEffect, useState } from 'react'
import Button from 'react-bootstrap/button'
import { useNavigate } from 'react-router-dom';
import { ShopifyContext } from '../ShopifyContext'



const Method = () => {
    const navigate = useNavigate();
    const { fetchCart,cart,updateShippingMethod } = useContext(ShopifyContext);

    useEffect(() => {
        updateShippingMethod(localStorage.cart_id);
        return () => {
        }
      },[getShippingMethod])

    const sm = cart.shippingMethd;

    const [dhl, setDhl] = useState(sm?.dhl);
    const [usps, setUsps] = useState(sm?.usps);
    const [eusps, setEusps] = useState(sm?.eusps);

    const updateShippMethod = () => {
        

        const shippingMethd = {
        dhl,
        usps,
        eusps
        };

        updateShippingMethod(localStorage.cart.id, shippingMethd);
        navigate('/payment');
    }

    const navigateToPayment = async () => {
        // await updateAddress (ShippingAddress, Address);
        navigate('/payment');
    }
    return (


        <div class='row'>
            <p>Credit Card</p>
            <input type="radio" value="DHL Express Worldwide" name="Shipping method" onChange={e => setDhl(e.target.value)} /><h4>DHL Express Worldwide</h4><h5>3 to 5 business Days</h5>
            <input type="radio" value="	USPS Priority Mail International" name="Shipping method" onChange={e => setUsps(e.target.value)} /><h4> USPS Priority Mail International</h4><h5>6 to 10 business Days</h5>
            <input type="radio" value="USPS Priority Mail Express International" name="Shipping method" onChange={e => setEusps(e.target.value)} /><h4> USPS Priority Mail Express International</h4><h5>3 to 5 business Days</h5>
            <Button onClick={() => navigateToPayment(updateShippMethod)}> Procced to Payment</Button>
        </div>

    )

    
}

export default Method;
